    DATEDIFF(date_retour, date_location) AS jours, montant_location 
FROM locations AS l
    INNER JOIN clients AS c USING(cin)
    INNER JOIN vehicules AS v USING (num_vehicule)
WHERE MONTH(date_location) = 1)

